mapper.select:
SELECT product.category_id category, product.id id, product.description description, product.content content, display_info.place_name placeName, file_info.save_file_name fileName FROM product JOIN display_info ON product.id = display_info.product_id JOIN product_image ON product.id=product_image.product_id JOIN file_info ON product_image.file_id = file_info.id WHERE product_image.type='th'
AND product.category_id=${categoryId}
LIMIT ${startIndex} , ${maxCount}
