mapper.select:
SELECT display_info.id displayInfoId, product.id id, product.description productDescription, product.content productContent, display_info.place_name placeName, file_info.save_file_name productImageUrl FROM product JOIN display_info ON product.id = display_info.product_id JOIN product_image ON product.id=product_image.product_id JOIN file_info ON product_image.file_id = file_info.id WHERE product_image.type=#{imageType}
AND product.category_id=#{categoryId}
LIMIT #{startIndex} , #{maxCount}
